.global-menu__container {
  opacity: 0;
  text-align: center;
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  margin: auto;
  height: 25rem;

  .global-menu__ttl {
    margin: 0 0 10px 0;
  }
}

.global-menu {
  display: none;
  position: fixed;
  z-index: 999;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 100%;
  overflow: hidden;
  background-color: #fcfcfc;
}

.global-menu--active {
  display: block;
  //&.
}

.menu-toggle {
  position: fixed;
  top: 7%;
  top: 1.5vw;
  right: 7%;
  right: 6vw;
  cursor: pointer;
  width: 50px;
  height: 50px;
  text-align: center;
  opacity: 1;
  z-index: 1000;

  .menu-hamburger {
    position: absolute;
    top: 50%;
    left: 50%;
    display: block;
    width: 26px;
    height: 1px;
    margin: -2px 0 0 -13px;
    background-color: #a1a9b0;
    text-indent: -9999px;
    transition: .2s ease-in-out;

    &:before {

      display: block;
      content: "";
      position: absolute;
      top: -8px;
      left: 0;
      width: 26px;
      height: 1px;
      border-radius: 2px;
      background-color: #a1a9b0;
      transition: .2s ease-in-out;

    }

    &:after {
      display: block;
      content: "";
      position: absolute;
      top: 8px;
      left: 0;
      width: 26px;
      height: 1px;
      border-radius: 2px;
      background-color: #a1a9b0;
      transition: .2s ease-in-out;
    }
  }

  &.menu-active {
    .menu-hamburger {
      background-color: transparent;

      &:before {
        width: 45px;
        transform: translateY(8px) translateX(-9px) rotate(135deg);
        -ms-transform: translateY(8px) translateX(-9px) rotate(135deg);
        -moz-transform: translateY(8px) translateX(-9px) rotate(135deg);
        -webkit-transform: translateY(8px) translateX(-9px) rotate(135deg);
      }

      &:after {
        width: 45px;
        transform: translateY(-8px) translateX(-9px) rotate(-135deg);
        -ms-transform: translateY(-8px) translateX(-9px) rotate(-135deg);
        -moz-transform: translateY(-8px) translateX(-9px) rotate(-135deg);
        -webkit-transform: translateY(-8px) translateX(-9px) rotate(-135deg);
      }
    }
  }
}

.header-container {
  position: fixed;
  left: 0;
  top: 0;
  width: 100%;
  z-index: 10;
}

.header-scroll {
  height: 60px;
  background-color: rgba(255, 255, 255, 1);
  animation: top-in-basic 1 1s 0s;
  -webkit-box-shadow: 0px 0px 2px 2px #ccc;
  box-shadow: 0px 0px 2px 2px #ccc;
}

.work-list {
  li {
    margin-bottom: 1%;

    img {
      width: 100%;
    }
  }
}



.hover1 {
  display: inline-block;
  position: relative;
  text-decoration: none;
}

.hover1::after {
  position: absolute;
  content: '';
  bottom: 0;
  left: 0;
  width: 0;
  height: 1px;
  background: #2196F3;
  transition: all 0.3s ease 0s;
}

.hover1:hover {
  cursor: pointer;
}

.hover1:hover::after {
  width: 100%;
}
